item.description:
int foo;
short *bar = &foo;

void func(void)
{
    int foo2;
    short *bar2;
    bar2 = & foo2;
}

char       a;
int        b;
long       c;
long long  d;

char      *e = &b;
int       *f = &a;
long      *g = &d;
long long *h = &c;

// note that "long" and "long long" are the
// same size on this compiler, but still warn
typedef int test_sizes[sizeof(long long) == sizeof(long) ? 1 : -1];

// whereas int32_t* and int* does not warn
int32_t *i = &b;

// and signed/unsigned is a different warning
unsigned int *j = &b;
